  return (
    <div className="cart-item">
      <img src={imageUrl} alt="item"></img>
      <div className="item-details">
        <span className="name">{name}</span>
        <span className="price">
          {quantity} x {price}â‚¬
        </span>
      </div>
    </div>
  );
};
export default CartItem;
